import { ObjectId } from 'mongodb';
import { v4 as uuidv4 } from 'uuid';
import dbClient from '../utils/db';
import redisClient from '../utils/redis';

const mime = require('mime-types');
const fs = require('fs');

class FilesController {
  /**
   * It creates a new file or folder in the database
   * @param req - The request object.
   * @param res - The response object.
   * @returns - If the user is not authenticated, it returns a 401 error.
   *   - If the user is authenticated, it returns a 201 status code and the file information.
   */
  static async postUpload(req, res) {
    const key = req.header('X-Token');
    const session = await redisClient.get(`auth_${key}`);
    if (!key || key.length === 0) {
      return res.status(401).json({ error: 'Unauthorized' });
    }
    if (session) {
      const { name, type, isPublic } = req.body;
      let { parentId } = req.body;
      const { data } = req.body;
      const types = ['folder', 'file', 'image'];
      if (!name) {
        return res.status(400).json({ error: 'Missing name' });
      }
      if (!type || types.includes(type) === false) {
        return res.status(400).json({ error: 'Missing type' });
      }
      if (!data && type !== types[0]) {
        return res.status(400).json({ error: 'Missing data' });
      }
      if (!parentId) {
        parentId = 0;
      }
      if (parentId !== 0) {
        const searchFile = await dbClient.db
          .collection('files')
          .find({ _id: ObjectId(parentId) })
          .toArray();
        if (searchFile.length < 1) {
          return res.status(400).json({ error: 'Parent not found' });
        }
        if (types[0] !== searchFile[0].type) {
          return res.status(400).json({ error: 'Parent is not a folder' });
        }
      }
      const userId = session;
      if (type === types[0]) {
        const objectToInsert = {
          name,
          type,
          userId: ObjectId(userId),
          parentId: parentId === 0 ? 0 : ObjectId(parentId),
          isPublic: isPublic || false,
        };
        const folder = await dbClient.db
          .collection('files')
          .insertOne(objectToInsert);
        const objectToReturn = {
          id: folder.ops[0]._id,
          userId: folder.ops[0].userId,
          name: folder.ops[0].name,
          type: folder.ops[0].type,
          isPublic: folder.ops[0].isPublic,
          parentId: folder.ops[0].parentId,
        };
        return res.status(201).json(objectToReturn);
      }

      const buff = Buffer.from(data, 'base64').toString('utf-8');
      const path = process.env.FOLDER_PATH || '/tmp/files_manager';
      const newFile = uuidv4();

      if (!fs.existsSync(path)) {
        fs.mkdirSync(path, { recursive: true });
      }
      fs.writeFile(`${path}/${newFile}`, buff, (err) => {
        if (!err) return true;
        return res.status(400).json({ error: err.message });
      });
      const objectToInsert = {
        name,
        type,
        userId: ObjectId(userId),
        parentId: parentId !== 0 ? ObjectId(parentId) : 0,
        isPublic: isPublic || false,
        data,
        localPath: `${path}/${newFile}`,
      };
      const file = await dbClient.db
        .collection('files')
        .insertOne(objectToInsert);
      const objectToReturn = {
        id: file.ops[0]._id,
        userId: file.ops[0].userId,
        name: file.ops[0].name,
        type: file.ops[0].type,
        isPublic: file.ops[0].isPublic,
        parentId: file.ops[0].parentId,
      };
      return res.status(201).json(objectToReturn);
    }
    return res.status(401).json({ error: 'Unauthorized' });
  }

  /**
   * It gets a file from the database
   * @param req - The request object.
   * @param res - The response object.
   * @returns The file information is being returned.
   */
  static async getShow(req, res) {
    const key = req.header('X-Token');
    const session = await redisClient.get(`auth_${key}`);
    if (!key || key.length === 0) {
      return res.status(401).json({ error: 'Unauthorized' });
    }
    if (session) {
      const { id } = req.params;
      const search = await dbClient.db
        .collection('files')
        .find({ _id: ObjectId(id) })
        .toArray();
      if (!search || search.length < 1) {
        return res.status(404).json({ error: 'Not found' });
      }
      const objectToReturn = {
        id: search[0]._id,
        userId: search[0].userId,
        name: search[0].name,
        type: search[0].type,
        isPublic: search[0].isPublic,
        parentId: search[0].parentId,
      };
      return res.json(objectToReturn);
    }
    return res.status(401).json({ error: 'Unauthorized' });
  }

  /**
   * It gets the files from the database and returns them to the user
   * @param req - The request object.
   * @param res - The response object.
   * @returns An array of files
   */
  static async getIndex(req, res) {
    const key = req.header('X-Token');
    const session = await redisClient.get(`auth_${key}`);
    if (!key || key.length === 0) {
      return res.status(401).json({ error: 'Unauthorized' });
    }
    if (session) {
      let { parentId } = req.query;
      if (!parentId) {
        parentId = '0';
      }
      if (parentId === '0') {
        const searchFile = await dbClient.db
          .collection('files')
          .find({ parentId: parseInt(parentId, 10) })
          .toArray();
        if (searchFile) {
          return res.status(200).send(searchFile);
        }
      } else if (parentId !== 0) {
        const searchFile = await dbClient.db
          .collection('files')
          .find({ parentId: ObjectId(parentId) })
          .toArray();
        if (searchFile) {
          return res.status(200).send(searchFile);
        }
      }
    }
    return res.status(401).json({ error: 'Unauthorized' });
  }

  /**
   * It takes a file id and a token, checks if the token is valid, checks if the file exists,
   * checks if the file belongs to the user, and if all of that is true, it sets the file to public
   * @param req - The request object.
   * @param res - The response object.
   * @returns The file is being returned.
   */
  static async putPublish(req, res) {
    const key = req.header('X-Token');
    const session = await redisClient.get(`auth_${key}`);
    if (!key || key.length === 0) {
      return res.status(401).json({ error: 'Unauthorized' });
    }
    if (session) {
      const { id } = req.params;
      if (!id || id === '') {
        return res.status(401).json({ error: 'Unauthorized' });
      }
      let search = [];
      try {
        search = await dbClient.db
          .collection('files')
          .find({ _id: ObjectId(id), userId: ObjectId(session) })
          .toArray();
      } catch (e) {
        return res.status(404).json({ error: 'Not found' });
      }
      if (!search || search.length < 1) {
        return res.status(404).json({ error: 'Not found' });
      }
      await dbClient.db
        .collection('files')
        .updateOne({ _id: ObjectId(id) }, { $set: { isPublic: true } });
      const searchAux = await dbClient.db
        .collection('files')
        .find({ _id: ObjectId(id), userId: ObjectId(session) })
        .toArray();
      if (!searchAux || searchAux.length < 1) {
        return res.status(404).json({ error: 'Not found' });
      }
      const objectToReturn = {
        id: searchAux[0]._id,
        userId: searchAux[0].userId,
        name: searchAux[0].name,
        type: searchAux[0].type,
        isPublic: searchAux[0].isPublic,
        parentId: searchAux[0].parentId,
      };
      return res.status(200).json(objectToReturn);
    }
    return res.status(401).json({ error: 'Unauthorized' });
  }

  /**
   * It takes a file id and a user id, and sets the file's isPublic property to false
   * @param req - The request object.
   * @param res - The response object.
   * @returns The file is being returned.
   */
  static async putUnpublish(req, res) {
    const key = req.header('X-Token');
    const session = await redisClient.get(`auth_${key}`);
    if (!key || key.length === 0) {
      return res.status(401).json({ error: 'Unauthorized' });
    }
    if (session) {
      const { id } = req.params;
      if (!id || id === '') {
        return res.status(401).json({ error: 'Unauthorized' });
      }
      let search = [];
      try {
        search = await dbClient.db
          .collection('files')
          .find({ _id: ObjectId(id), userId: ObjectId(session) })
          .toArray();
      } catch (e) {
        return res.status(404).json({ error: 'Not found' });
      }
      if (!search || search.length < 1) {
        return res.status(404).json({ error: 'Not found' });
      }
      await dbClient.db
        .collection('files')
        .updateOne({ _id: ObjectId(id) }, { $set: { isPublic: false } });
      const searchAux = await dbClient.db
        .collection('files')
        .find({ _id: ObjectId(id), userId: ObjectId(session) })
        .toArray();
      if (!searchAux || searchAux.length < 1) {
        return res.status(404).json({ error: 'Not found' });
      }
      return res.status(200).json({
        id: searchAux[0]._id,
        userId: searchAux[0].userId,
        name: searchAux[0].name,
        type: searchAux[0].type,
        isPublic: searchAux[0].isPublic,
        parentId: searchAux[0].parentId,
      });
    }
    return res.status(401).json({ error: 'Unauthorized' });
  }

  /**
   * It gets the file from the database, checks if it's public or not,
   * and if it's not public, it checks
   * if the user is authenticated, and if it is, it returns the file
   * @param req - The request object
   * @param res - The response object.
   * @returns The file content
   */
  static async getFile(req, res) {
    const { id } = req.params;
    if (!id || id === '') {
      return res.status(404).json({ error: 'Not found' });
    }
    let search = [];
    try {
      search = await dbClient.db
        .collection('files')
        .find({ _id: ObjectId(id) })
        .toArray();
    } catch (e) {
      return res.status(404).json({ error: 'Not found' });
    }
    if (!search || search.length < 1) {
      return res.status(404).json({ error: 'Not found' });
    }
    if (search[0].type === 'folder') {
      return res.status(400).json({ error: "A folder doesn't have content" });
    }
    if (search[0].isPublic === false) {
      const key = req.header('X-Token');
      const session = await redisClient.get(`auth_${key}`);
      if (!key || key.length === 0) {
        return res.status(404).json({ error: 'Not found' });
      }
      if (session) {
        let searchAux = [];
        try {
          searchAux = await dbClient.db
            .collection('files')
            .find({ _id: ObjectId(id), userId: ObjectId(session) })
            .toArray();
        } catch (e) {
          return res.status(404).json({ error: 'Not found' });
        }
        if (!searchAux || searchAux.length < 1) {
          return res.status(404).json({ error: 'Not found' });
        }
        if (!fs.existsSync(searchAux[0].localPath)) {
          return res.status(404).json({ error: 'Not found' });
        }

        const type = mime.contentType(searchAux[0].name);
        const charset = type.split('=')[1];
        try {
          const data = fs.readFileSync(searchAux[0].localPath, charset);
          return res.send(data);
        } catch (e) {
          return res.status(404).json({ error: 'Not found' });
        }
      }
      return res.status(404).json({ error: 'Not found' });
    }

    const defaultSearch = await dbClient.db
      .collection('files')
      .find({ _id: ObjectId(id) })
      .toArray();
    if (!defaultSearch || defaultSearch.length < 1) {
      return res.status(404).json({ error: 'Not found' });
    }
    if (!fs.existsSync(defaultSearch[0].localPath)) {
      return res.status(404).json({ error: 'Not found' });
    }
    const type = mime.contentType(defaultSearch[0].name);
    const charset = type.split('=')[1];
    try {
      const data = fs.readFileSync(defaultSearch[0].localPath, charset);
      return res.send(data);
    } catch (e) {
      return res.status(404).json({ error: 'Not found' });
    }
  }
}

export default FilesController;
